import React from 'react';
import "./Toggle";
import { Navbar, Nav, NavItem, NavProps, Button } from 'react-bootstrap';
import useToggle from './Toggle';

interface ToolBoxProps {
    id: number,
    login: string
}
 
interface ToolBoxState {
    expanded: boolean
}
 
class ToolBox extends React.Component<ToolBoxProps, ToolBoxState> {
    constructor(props: ToolBoxProps)
    {
        super(props);
    };

    state : ToolBoxState = {
        expanded: false
    };

    onClick = (e: React.MouseEvent<HTMLButtonElement, MouseEvent>): void => {
        let newExpanded = !this.state.expanded;
        this.setState({ expanded: newExpanded});
      };

    render() {
        return (
          <div id="toolbox">
            <div className="pos-f-t">
              <div style={
                  {MozTransitionTimingFunction: 'linear'}
              } className={this.state.expanded ? "expanded" : "collapse"} id="navbarToggleExternalContent">
                <div className="bg-dark p-4">
                  <h5 className="text-white h4">Collapsed content</h5>
                  <span className="text-muted">
                    Toggleable via the navbar brand.
                  </span>
                </div>
              </div>
              <nav className="navbar navbar-dark bg-dark">
                <button
                  className="navbar-toggler"
                  type="button"
                  aria-expanded = {this.state.expanded}
                  onClick={this.onClick}
                >
                  <span className="navbar-toggler-icon"></span>
                </button>
              </nav>
            </div>
          </div>
        );
    }
}
 
export default ToolBox;
