import * as React from 'react';
import { Form, Button, Stack } from 'react-bootstrap';
import { FieldValues, set, useForm } from 'react-hook-form';
import { LoginDto, User } from '../types/UserTypes';
import UserService from '../functions/UserService';
import { useState } from 'react';

const LoginForm = () => {
  const useForm = (callback: any, initialState: {}) => {
    const [values, setValues] = useState(initialState);

    const onChange = (event: React.ChangeEvent<HTMLInputElement>) => {
      setValues({...values, [event.target.name] : event.target.value});
    }

    const onSubmit = async (event: React.FormEvent<HTMLFormElement>) => {
      event.preventDefault();
      await callback();
    }

    return {
      onChange,
      onSubmit,
      values
    }
  }
    
  

    const submitForm = (data : FieldValues) => {
      let dto : LoginDto = {login: data.login, forgotPassword: data.forgotPassword, password: data.password};
      UserService.SignIn(dto);
    }

    return(
             <div id="login-form" className="d-flex justify-content-center">
               <form 
               onSubmit={submitForm}
               className="border rounded border-primary mt-3">
                 <Stack gap={0} direction="horizontal" className="m-5">
                   <h1 className="d-flex justify-content-center">Sign in</h1>
                   <hr />
                   <input
                     type="text"
                     className="form-control"
                     id="login-login"
                     placeholder="Login"
                     required
                     name = "login"
                     value={login}
                   />
                   <br />
                   <input
                     type="password"
                     className="form-control"
                     id="login-password"
                     placeholder="Password"
                     name="password"
                     disabled={forgotPassword}
                     value={password}
                   />
                   <div className="form-check mt-2">
                     <input
                       className="form-check-input"
                       type="checkbox"
                       name="forgotPassword"
                       id="login-forgot-password"
                       onChange={handleCheckboxChange}
                     />
                     <label
                       className="form-check-label mb-5"
                       htmlFor="login-forgot-password"
                     >
                       Forgot password?
                     </label>
                   </div>
                   <Button className='btn btn-primary col-md-12' type="submit">
                        Submit
                   </Button>
                   <hr/>
                   <Button className='btn btn-secondary col-md-12'>
                        Sign up for free
                   </Button>
                 </Stack>
               </form>
             </div>
           );
       };

export default LoginForm;
